
-- creation de tables

--JOUEUR

CREATE TABLE JOUEUR 
(ID_joueur int AUTO_INCREMENT PRIMARY KEY,
 NOM VARCHAR(30),
 PRENOM VARCHAR(30),
 CATEGORIE VARCHAR(30),
 LICENCIE VARCHAR(30),
 constraint lacategorie check ('CATEGORIE' in ('senior')),
 constraint lalicence check ('LICENCE' in('oui','non')));





--CALENDRIER
CREATE TABLE CALENDRIER 
(CATEGORIE VARCHAR(30),
 COMPETITION VARCHAR(30),
 EQUIPE VARCHAR(30),
 EQUIPEADVERSE VARCHAR(30),
 DATE_RENCONTRE DATE NOT NULL,
 HEURE TIME NOT NULL,
 VILLE VARCHAR(30),
 SITE VARCHAR(30),
 constraint lacategorie check ('CATEGORIE' in ('senior')));




--ABSENCE
CREATE TABLE ABSENCE 
( ID_joueur int,
  NOM VARCHAR(30),
  PRENOM VARCHAR(30),
  DATE_ABSENCE DATE NOT NULL,
  DISPONIBILITE VARCHAR(30),
  foreign key (ID_joueur) references JOUEUR(ID_joueur) ON UPDATE CASCADE ON DELETE CASCADE,
  constraint labsence check ('DISPONIBILITE' in ('absent','blessé','non-licencié','suspendu')));


--ADMINISTRATEURS

CREATE TABLE ADMINISTRATEURS 
(NOM VARCHAR(30),
 PASSWORD VARCHAR(30),
 ROLE VARCHAR(30),
 constraint lerole check ('ROLE' in ('secrétaire','entraîneur')));


